			//#endregion
			<div className='grid-container' >
				<div className="header">
					<Header />
				</div>
				<div className="menu" >
					{/* 下面的这个whitespace是换行的一个操作 */}
					<div className="environment">
						<NavLink to="/about">环保厅</NavLink>
					</div>
					<div className='home'>
						<NavLink to="/home">Home</NavLink>
					</div>
				</div>

				<div className="main">
					{/* 注册路由 */}
					<Switch>
						<Route path="/about" component={About} />
						<Route path="/home" component={Home} />
						<Redirect to="/about" />
					</Switch>
				</div>
			</div>
		)
	}
}
