import React, { Component } from 'react'
import { Route, Switch, Redirect } from 'react-router-dom'
import Home from './pages/Home' //Home是路由组件
import About from './pages/About' //About是路由组件
import List from './pages/List'//List是路由组件
import Thirty from './pages/Thirty'//Thirty是路由组件
import Environment_hbt from './pages/Environment_hbt'//Thirty是路由组件
import Header from './components/Header' //Header是一般组件
import { NavLink } from 'react-router-dom'
// import MyNavLink from './components/MyNavLink'
import './App.css'

export default class App extends Component {
	render() {
		return (
			//#region 
			// <div className='grid-container' >
			// 	<div className="row">
			// 		<div className="col-xs-offset-2 col-xs-8">
			// 			<Header/>
			// 		</div>
			// 	</div>
			// 	<div className="row">
			// 		<div className="col-xs-2 col-xs-offset-2">
			// 			<div className="list-group">
			// 				<MyNavLink to="/about">About</MyNavLink>
			// 				<MyNavLink to="/home">Home</MyNavLink>
			// 			</div>
			// 		</div>
			// 		<div className="col-xs-6">
			// 			<div className="panel">
			// 				<div className="panel-body">
			// 					{/* 注册路由 */}
			// 					<Switch>
			// 						<Route path="/about" component={About}/>
			// 						<Route path="/home" component={Home}/>
			// 						<Redirect to="/about"/>
			// 					</Switch>
			// 				</div>
			// 			</div>
			// 		</div>
			// 	</div>
			// </div>
			//#endregion
			<div className='grid-container' >
				<div className="header">
					<Header />
				</div>
				<div className="menu" >
					{/* 下面的这个whitespace是换行的一个操作 */}
					<div className="about">
						<NavLink to="/about" style={{ fontSize: "16px", color: 'white' }}>煤制乙二醇平台</NavLink>
					</div>
					<div className='home'>
						<NavLink to="/home" style={{ fontSize: "16px", color: 'white' }}>环保厅在线监测</NavLink>
					</div>
					<div className='list'>
						<NavLink to="/list" style={{ fontSize: "16px", color: 'white' }}>虚拟仿真平台</NavLink>
					</div>
					<div className='thirty'>
						<NavLink to="/thirty" style={{ fontSize: "16px", color: 'white' }}>重点研发30个APP</NavLink>
					</div>
					<div className='environment'>
						<NavLink to="/environment" style={{ fontSize: "16px", color: 'white' }}>环保厅在线监测</NavLink>
					</div>
				</div>
				<div className="main">
					{/* 注册路由 */}
					<Switch>
						<Route path="/about" component={About} />
						<Route path="/home" component={Home} />
						<Route path="/list" component={List} />
						<Route path="/thirty" component={Thirty} />
						<Route path="/thirty" component={Environment_hbt} />
						<Redirect to="/about" />
					</Switch>
				</div>
			</div>
		)
	}
}
